import React, { Component } from 'react'
import socketIOClient from 'socket.io-client'
const axios = require('axios')

const serverUrl = 'http://192.168.1.4:3001/'

class MainPage extends Component{

    state = {
        renderPlayers: null
    }

    
    componentDidMount(){
        axios({
            method: 'get',
            url: serverUrl + 'main-page/' + this.props.roomid
        })
        .then(response => {
            if(response.data.tag === "ok"){
                console.log(response.data.players.players)
                this.setState({
                    renderPlayers : (
                        <div>
                            {response.data.players.players.map(player => {return(<div key={player}><p>{player}</p></div>)})}
                        </div>
                    )
                    
                })
            }
        })
        .catch(err => console.log(err))
    }

    render(){
        return(
            <div>
                hello
                <br></br>
                {this.state.renderPlayers}
            </div>
        )
    }
}

export default MainPage
