import React, { Component } from 'react'
import { Redirect, Route, withRouter } from 'react-router-dom'

import MainPage from '../MainPage/MainPage'

const uniqid = require('uniqid')
const axios = require('axios')

const serverUrl = 'http://localhost:3001/'

let insertedRoomid

class Login extends Component{

    state={
        username: "",
        roomid: "",
        newButtonClicked: false,
        goButtonClicked: false
    }

    newButton = () => {
        this.setState(prevState => ({
            newButtonClicked: !prevState.newButtonClicked
        }))

        axios({
            method: 'post',
            url: serverUrl + 'register/new-room-id',
            params: {
                username: this.state.username,
                roomid: this.state.roomid
            }
            
        })
        .then(response => console.log(response))
        .catch(err => console.log(err))
    }


    goButton = () =>{
        axios({
            method: 'post',
            url: serverUrl + 'check-room',
            params: {
                username: this.state.username
            },
            data: {
                roomid: insertedRoomid
            }
        })
        .then(response => {
            if(response.data === "ok"){
                axios({
                    method: 'post',
                    url: serverUrl + 'main-page',
                    data:{
                        username: this.state.username,
                        roomid: insertedRoomid
                    }
                })
                .then(response => {
                    this.props.history.push(`/main-page`)
                })
                .catch(err => console.log(err))
            }
        })
        .catch(err => console.log(err))
    }

    componentDidMount(){
        var roomid = uniqid()

        this.setState({roomid: roomid})
    }

    componentDidUpdate(prevProps, prevState){
    }

    render(){
        return(
            <div className="Login-page-cover">
                <div className="Login-page-title">
                    <h2>Login title</h2>
                </div>
                <div className="Login-page-body">
                    <label>username</label>
                    <input type="text" name="player_username" onChange={e => this.setState({username: e.target.value})}/>
                    <button type="button" onClick={this.newButton}>create new username</button>
                    <br></br>
                    <br></br>
                    <label>room id</label>
                    <input type="text" name="player_roomid" id="player_roomid" onChange={e => insertedRoomid = e.target.value}/>
                    <button type="button" onClick={this.goButton}>go</button>
                </div>
                <div className="Login-page-tail">
                    {this.state.newButtonClicked ? this.state.roomid : null}
                </div>
            </div>
        )
    }
}

export default Login
