import React, { Component } from 'react'
import { Redirect, Route, withRouter } from 'react-router-dom'


const uniqid = require('uniqid')
const axios = require('axios')

class Login extends Component{

    state={
        username: "",
        generatedRoomNumber: null
    }

    goButton = () => {
        
        var roomId = uniqid()
        this.setState(prevState => ({
            generatedRoomNumber: roomId
        }))

        axios.post('http://localhost:3001/Register?username=' + this.state.username, {
            userInfo: this.state
        })
        .then(response => console.log(response))
        .catch(err => console.log(err))


    }

    componentDidMount(){
        
    }

    render(){
        return(
            <div className="Login-page-cover">
                <div className="Login-page-title">
                    <h2>Login title</h2>
                </div>
                <div className="Login-page-body">
                    <input type="text" name="player_username" onChange={e => this.setState({username: e.target.value})}/>
                    <button type="button" onClick={this.goButton}>Go</button>
                </div>
            </div>
        )
    }
}

export default Login
