import React, { Component } from 'react'
import { Redirect, Route, withRouter } from 'react-router-dom'

import MainPage from '../MainPage/MainPage'

const uniqid = require('uniqid')
const axios = require('axios')

const serverUrl = 'http://localhost:3001/'

class Login extends Component{

    state={
        username: "",
        roomid: "",
        newButtonClicked: false,
        goButtonClicked: false
    }

    newButton = () => {
        var roomid = uniqid()

        this.setState({roomid: roomid})

        this.setState(prevState => ({
            newButtonClicked: !prevState.newButtonClicked
        }))

        axios({
            method: 'post',
            url: serverUrl + 'Register/filter',
            params: {
                username: this.state.username,
                roomid: roomid
            }
            
        })
        .then(response => console.log(response))
        .catch(err => console.log(err))
    }


    goButton = () =>{
        var roomid = document.getElementById('player_roomid').innerText

        axios({
            method: 'post',
            url: serverUrl + 'CheckRoom',
            data: {
                roomid: roomid
            }
        })
        .then(response => {
            let ifRoomExists
        })
        .catch(err => console.log(err))
    }

    componentDidMount(){
        
    }

    componentDidUpdate(prevProps, prevState){
        if(this.state.username !== "" && this.state.newButtonClicked){
            
            axios.get(serverUrl + 'Register')
            .then(response => {
                console.log(response)
            })
            .catch(err => console.log(err))
        }
    }

    render(){
        return(
            <div className="Login-page-cover">
                <div className="Login-page-title">
                    <h2>Login title</h2>
                </div>
                <div className="Login-page-body">
                    <label>username</label>
                    <input type="text" name="player_username" onChange={e => this.setState({username: e.target.value})}/>
                    <button type="button" onClick={this.newButton}>new</button>
                    <br></br>
                    <br></br>
                    <label>room id</label>
                    <input type="text" name="player_roomid" id="player_roomid"/>
                    <button type="button" onClick={this.goButton}>go</button>
                </div>
                <div className="Login-page-tail">
                    {this.state.newButtonClicked ? this.state.roomid : null}
                </div>
            </div>
        )
    }
}

export default Login
