import React, { Component } from 'react'
import { Redirect, Route, withRouter } from 'react-router-dom'


const uniqid = require('uniqid')
const axios = require('axios')

class Login extends Component{

    state={
        username: "",
        generatedRoomId: null
    }

    goButton = () => {
        var roomId = uniqid()
        this.setState({generatedRoomId: roomId})

    }

    componentDidMount(){
    }

    componentDidUpdate(prevProps, prevState){
        if(this.state.username !== prevState.username && this.state.generatedRoomId !== prevState.generatedRoomId){
            console.log(true)
        }
    }

    render(){
        return(
            <div className="Login-page-cover">
                <div className="Login-page-title">
                    <h2>Login title</h2>
                </div>
                <div className="Login-page-body">
                    <input type="text" name="player_username" onChange={e => this.setState({username: e.target.value})}/>
                    <button type="button" onClick={this.goButton}>Go</button>
                </div>
            </div>
        )
    }
}

export default Login
