import React, { Component } from 'react'
import socketIOClient from 'socket.io-client'
const axios = require('axios')

const serverUrl = 'http://192.168.1.4:3001/'

class MainPage extends Component{

    state = {
        renderPlayers: null
    }

    componentDidMount(){
        const socket = socketIOClient(serverUrl + '/main-page')
        socket.on("GetPlayers", data => {this.setState({renderPlayers: data.players.map(player => {return(<div key = {player}><p>{player}</p></div>)})})})
    }

    render(){
        return(
            <div>
                hello
                <br></br>
                {this.state.renderPlayers}
            </div>
        )
    }
}

export default MainPage
