        //     if(response.data === "ok"){
        const socket = socketIOClient(serverUrl + 'main-page', {
            query: {
                roomid : this.props.roomid
            }
        } )
        socket.on("GetPlayers", data => {this.setState({renderPlayers: data.map(player => {return(<div key = {player}><p>{player}</p></div>)})})})
        socket.on("GetPlayers", data => this.setState({renderPlayers: data}))
            // }
            
        // })
        // .catch(err => console.log(err))
        
    }

    render(){
        return(
            <div>
                hello
                <br></br>
                {this.state.renderPlayers}
            </div>
        )
    }
}

export default MainPage
