import React, { Component } from 'react'
import socketIOClient from 'socket.io-client'
import Cookies from 'universal-cookie'


const cookies = new Cookies()
const serverUrl = 'http://192.168.1.4:3001/'



class StartBttn extends Component{

    state = {
        ifAdmin: false,
        admin: ""
    }

    

    componentDidMount(){
        const socket = socketIOClient(serverUrl + 'get-admin', {
            query: {
                roomid: this.props.roomid,
                username: this.props.username
            }
        })

        socket.on('GetAdmin', data => {
            this.setState({
                admin: data
            })

            if(this.props.username == data){
                this.setState({
                    ifAdmin: true
                })
            }
            else{
                admin = ""
                this.setState({
                    ifAdmin: false
                })
            }
        })
    }

    render(){
        return(
            <>
                {this.state.ifAdmin ? 
                    <>
                    <b>You are the admin</b>
                    <br></br>
                    <br></br>
                    <button type='button' >test cookies</button>
                    </>
                    :

                    null
                    
                }
                
                <p>Admin is: {this.state.admin}</p>
            </>
        )
    }
}

export default StartBttn
