import React, { Component } from 'react'
import { Redirect, Route, withRouter } from 'react-router-dom'


const uniqid = require('uniqid')
const axios = require('axios')

class Login extends Component{

    state={
        username: "",
        generatedRoomId: null
    }

    goButton = () => {
        var roomId = uniqid()
        this.setState({generatedRoomId: roomId})
        this.setState(prevState => ({
            display: !prevState.display
        }))
    }

    componentDidMount(){
        
    }

    componentDidUpdate(prevProps, prevState){
        if(this.state.generatedRoomId !== prevState.generatedRoomId && this.state.username !== ""){
            axios.post('http://localhost:3001/Register', {
                username: this.state.username,
                roomid: this.state.generatedRoomId
            })
            .then(response => console.log(response))
            .catch(err => console.log(err))

            axios.get('http://localhost:3001/Register')
            .then(response => {
                console.log(response)
            })
            .catch(err => console.log(err))
        }
    }

    render(){
        return(
            <div className="Login-page-cover">
                <div className="Login-page-title">
                    <h2>Login title</h2>
                </div>
                <div className="Login-page-body">
                    <label>username</label>
                    <input type="text" name="player_username" onChange={e => this.setState({username: e.target.value})}/>
                    <button type="button" onClick={this.goButton}>new</button>
                    <br></br>
                    <br></br>
                    <label>room id</label>
                    <input type="text" name="player_username"/>
                    <button type="button">go</button>
                </div>

            </div>
        )
    }
}

export default Login
