import React, { Component } from 'react'
import socketIOClient from 'socket.io-client'
import Cookies from 'universal-cookie'


const cookies = new Cookies()
const serverUrl = 'http://192.168.1.4:3001/'


class StartBttn extends Component{

    state = {
        ifAdmin: false
    }

    

    componentDidMount(){
        const socket = socketIOClient(serverUrl + 'get-admin', {
            query: {
                'roomid': this.props.roomid,
                'username': this.props.username
            }
        })

        socket.on('GetAdmin', data => {
            if(this.props.username == data)
                this.setState({
                    ifAdmin: true
                })
            else
                this.setState({
                    ifAdmin: false
                })
            
        })
    }

    render(){
        return(
            <>
                {this.state.ifAdmin ? 

                    <b>You are the admin</b>

                    :

                    null
                    
                }
                <button type='button' >test cookies</button>
            </>
        )
    }
}

export default StartBttn
