import React, { Component } from 'react'
import socketIOClient from 'socket.io-client'

const serverUrl = 'http://192.168.1.4:3001/'

class DisplayPlayerNames extends Component{
    state = {
        renderPlayers: null
    }

    componentDidMount(){
        const socket = socketIOClient(serverUrl + 'main-page', {
            query: {
                roomid : this.props.roomid
            }
        } )
        socket.on('GetPlayers', data => {this.setState({renderPlayers: data.map(player => {return(<div key = {player}><p>{player}</p></div>)})})})
        
    }

    render(){
        return(
            <div>
                
            </div>
        )
    }
}


export default DisplayPlayerNames
