import React, { Component } from 'react'
import { Redirect, Route, withRouter } from 'react-router-dom'

import MainPage from '../MainPage/MainPage'

const uniqid = require('uniqid')
const axios = require('axios')

const serverUrl = 'http://localhost:3001/'

let insertedRoomid


class Login extends Component{

    state={
        username: "",
        roomid: "",
        newUserBttnClicked: false,
        newRoomBttnClicked: false,
        joinBttnClicked: false,
        usernameExists: false
    }

    newUserButton = () => {
        if(this.state.username !== "" || this.state.username.length !== 0){
            axios({
                method: 'post',
                url: serverUrl + 'players/create',
                data: {
                    username: this.state.username,
                    timeCreated: Date.now()
                }
            })
            .then(response => {
                if(response.data === "ok"){
                    this.setState({
                        newUserBttnClicked: true,
                        usernameExists: false
                    })
                }
                
                else if (response.data === "username exists"){
                    this.setState({
                        usernameExists: true,
                        newUserBttnClicked: false
                    })
                }

            })
            .catch(err => {
                console.log(err)
            })
        }

        else{
            this.setState({
                newUserBttnClicked: false
            })
        }
    }

    newRoomButton = () => {
        var roomid = uniqid()

        this.setState({
            roomid: roomid
        })

        axios({
            method: 'get',
            url: serverUrl + 'players/' + this.state.username.toString().replace(' ', '-'),
            responseType: 'text'
        })
        .then(response => {
            if(response.data === "ok"){
                axios({
                    method: 'post',
                    url: serverUrl + 'rooms/create',
                    data: {
                        roomid: roomid,
                        admin: this.state.username,
                        timeCreated: Date.now(),
                        numberOfPlayers: 1,
                        moves: [] 
                    }
                })
                .then(response => {
                    if(response.data === "ok"){
                        this.setState(
                        {
                            newRoomBttnClicked  : true
                        })
                    }

                    else{
                        this.setState(
                        {
                            newRoomBttnClicked  : false
                        })
                    }
                })
                .catch(err => {
                    console.log(err)
                })
            }

            else{
                this.setState(
                {
                    newRoomBttnClicked  : false
                })
            }
        })
        .catch(err => {
            console.log(err)
        })
    }

    joinButton = () =>{
        axios({
            method: 'get',
            url: serverUrl + 'rooms/' + insertedRoomid,
        })
        .then(response => {
            if(response.data === "ok"){
                axios({
                    method: 'post',
                    url: serverUrl + 'rooms/' + insertedRoomid + '/join-new',
                    data:{
                        username: this.state.username,
                        roomid: insertedRoomid,
                        timeCreated: Date.now()
                    }
                })
                .then(response => {
                    if(response.data === "ok")
                        this.props.history.push(`/main-page`)
                })
                .catch(err => console.log(err))
            }
        })
        .catch(err => console.log(err))
    }

    componentDidMount(){

    }

    componentDidUpdate(prevProps, prevState){
    }

    render(){
        return(
            <div className="Login-page-cover">
                <div className="Login-page-title">
                    <h2>Login title</h2>
                </div>
                <div className="Login-page-body">
                    <label>username </label>
                    <input type="text" name="player_username" onChange={e => this.setState({username: e.target.value})}/>
                    <button type="button" onClick={this.newUserButton}>create new username</button>

                    {this.state.newUserBttnClicked ? 
                        <p>Username created successfully</p>
                        :
                        <p>Please enter a username</p>
                    }
                    <br></br>

                    <label>If you want to generate a new play room, click the following button </label>
                    <button type="button" onClick={this.newRoomButton}>create new room</button>

                    {this.state.newRoomBttnClicked ? 
                        <p>New room created, here is the room id: {this.state.roomid}</p>
                        :
                        null
                    }
                    <br></br>

                    <label>if you already have the generated room id, please type to join </label>
                    <input type="text" name="player_roomid" id="player_roomid" onChange={e => insertedRoomid = e.target.value}/>
                    <button type="button" onClick={this.joinButton}>join</button>
                </div>
            </div>
        )
    }
}

export default Login
